import React, { useState, useEffect } from 'react';
import axios from '../api/axios';

function AdminDashboard() {
  const [job, setJob] = useState({
    companyName: '',
    eligibility: '',
    deadline: ''
  });

  const [jobs, setJobs] = useState([]);

  // Handle input change
  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  // Submit new job
  const postJob = async () => {
    try {
      await axios.post('/admin/post-job', job); // make sure this endpoint exists
      alert('Job posted successfully!');
      setJob({ companyName: '', eligibility: '', deadline: '' });
      fetchJobs();
    } catch (err) {
      alert('Error posting job.');
      console.error(err);
    }
  };

  // Fetch all jobs
  const fetchJobs = async () => {
    try {
      const res = await axios.get('/admin/jobs'); // make sure this endpoint exists
      setJobs(res.data);
    } catch (err) {
      alert('Failed to load jobs');
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  return (
    <div className="container mt-5">
      <h2>Admin Dashboard</h2>

      {/* Job Posting Form */}
      <div className="card p-4 mt-3">
        <h4>Post a New Job</h4>
        <input
          type="text"
          name="companyName"
          value={job.companyName}
          onChange={handleChange}
          className="form-control my-2"
          placeholder="Company Name"
        />
        <input
          type="text"
          name="eligibility"
          value={job.eligibility}
          onChange={handleChange}
          className="form-control my-2"
          placeholder="Eligibility Criteria"
        />
        <input
          type="date"
          name="deadline"
          value={job.deadline}
          onChange={handleChange}
          className="form-control my-2"
        />
        <button className="btn btn-primary" onClick={postJob}>
          Post Job
        </button>
      </div>

      {/* Posted Jobs Table */}
      <div className="card p-4 mt-5">
        <h4>All Posted Jobs</h4>
        <table className="table mt-3">
          <thead>
            <tr>
              <th>Company</th>
              <th>Eligibility</th>
              <th>Deadline</th>
            </tr>
          </thead>
          <tbody>
            {jobs.map((j, index) => (
              <tr key={index}>
                <td>{j.companyName}</td>
                <td>{j.eligibility}</td>
                <td>{j.deadline}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Selected Students (Placeholder for now) */}
      <div className="card p-4 mt-5">
        <h4>Selected Students (Coming Soon)</h4>
        <p>Feature under development – will show list after student applications.</p>
      </div>
    </div>
  );
}

export default AdminDashboard;
