import React, { useState } from 'react';
import axios from '../api/axios';

function RegisterStudent() {
  const [student, setStudent] = useState({
    username: '',
    password: '',
    name: '',
    cgpa: '',
    tenthPercent: '',
    twelfthPercent: '',
    diplomaPercent: '',
    internshipDetails: ''
  });

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const register = async () => {
    try {
      await axios.post('/auth/register-student', student);
      alert('Student registered successfully');
    } catch (err) {
      alert('Registration failed');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Student Registration</h2>
      <input name="username" placeholder="Username" className="form-control" onChange={handleChange} />
      <input name="password" type="password" placeholder="Password" className="form-control mt-2" onChange={handleChange} />
      <input name="name" placeholder="Full Name" className="form-control mt-2" onChange={handleChange} />
      <input name="cgpa" placeholder="CGPA" className="form-control mt-2" onChange={handleChange} />
      <input name="tenthPercent" placeholder="10th %" className="form-control mt-2" onChange={handleChange} />
      <input name="twelfthPercent" placeholder="12th %" className="form-control mt-2" onChange={handleChange} />
      <input name="diplomaPercent" placeholder="Diploma %" className="form-control mt-2" onChange={handleChange} />
      <input name="internshipDetails" placeholder="Internship Details" className="form-control mt-2" onChange={handleChange} />
      <button className="btn btn-success mt-3" onClick={register}>Register</button>
    </div>
  );
}

export default RegisterStudent;
