import React, { useState } from 'react';
import axios from '../api/axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const res = await axios.post('/auth/login', { username, password });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('role', res.data.role);

      if (res.data.role === 'STUDENT') {
        navigate('/student/dashboard');
      } else if (res.data.role === 'ADMIN') {
        navigate('/admin/dashboard');
      }
    } catch (err) {
      alert('Login failed');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <input placeholder="Username" className="form-control" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" className="form-control mt-2" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button className="btn btn-primary mt-3" onClick={handleLogin}>Login</button>
    </div>
  );
}

export default Login;
