import React, { useState } from 'react';
import axios from '../api/axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const res = await axios.post('/auth/login', {
        username,
        password
      });

      // Save JWT and role
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('role', res.data.role);

      // Redirect by role
      if (res.data.role === 'ADMIN') {
        navigate('/admin/dashboard');
      } else if (res.data.role === 'STUDENT') {
        navigate('/student/dashboard');
      } else {
        setError('Unknown role.');
      }
    } catch (err) {
      setError('Invalid credentials');
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: '500px' }}>
      <h2 className="text-center">Campus Recruitment Login</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <input
        className="form-control my-2"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        className="form-control my-2"
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button className="btn btn-primary w-100 mt-2" onClick={handleLogin}>
        Login
      </button>

      <div className="mt-3 text-center">
        <p>Don't have an account?</p>
        <a href="/register-student" className="btn btn-outline-success mx-2">Student Register</a>
        <a href="/register-admin" className="btn btn-outline-warning">Admin Register</a>
      </div>
    </div>
  );
}

export default Login;
